sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {
	var tableName = current.getTableName();

	//Add Table definition and structe
	var buttonLink = '<table border="0" cellspacing="0" cellpadding="0"><tbody><tr><td style="font-size: 18px; font-family: Open Sans, Helvetica, Arial, sans-erif; border-radius: 22px; padding: 8px 25px;" align="center" bgcolor="#005596"><a style="color: #ffffff; text-decoration: none; border-radius: 22px; display: block;" href="';

	//built href
	buttonLink += '/hrportal?id=pdr_user_eval&table=' + tableName + '&sys_id=' + current.getUniqueValue() + '"';

	//complete table
	buttonLink += '>Open in My HR Tools &rarr;</a></td></tr></tbody></table>';

	//print Template
	template.print(buttonLink);

})(current, template, email, email_action, event);
